#!/usr/bin/env python

"""Solution for the Advent of Code challenge 2016, day 2 part 1.

Taking the center of the square as (0, 0), make sure that x and y never go over 1 or below -1."""

__author__ = "Serge Beaumont"
__date__ = "December 2016"

DATA = """



"""

x = y = 0
buttons = {(-1, 1): '1', (0, 1): '2', (1, 1): '3', (-1, 0): '4', (0, 0): '5', (1, 0): '6', (-1, -1): '7', (0, -1): '8', (1, -1): '9'}
code = ''

for line in DATA.split('\n'):
    for c in line:
        if (c == "R") and (x < 1):
            x += 1
        elif (c == "U") and (y < 1):
            y += 1
        elif (c == "L") and (x > -1):
            x += -1
        elif (c == "D") and (y > -1):
            y += -1
    code += buttons[(x, y)]

print ("The code is", code)