#!/usr/bin/env python

"""Solution for the Advent of Code challenge 2016, day 2 part 2.

The trick here is to realize that the distance to the center may never be larger than 3."""

__author__ = "Serge Beaumont"
__date__ = "December 2016"

DATA = """



"""

x = -2
y = 0
buttons = {
    (0, 2): '1',
    (-1, 1): '2', (0, 1): '3', (1, 1): '4',
    (-2, 0): '5', (-1, 0): '6', (0, 0): '7', (1, 0): '8', (2, 0): '9',
    (-1, -1): 'A', (0, -1): 'B', (1, -1): 'C',
    (0, -2): 'D'}
code = ''

for line in DATA.split('\n'):
    for c in line:
        oldX = x
        oldY = y
        if (c == "R"):
            x += 1
        elif (c == "U"):
            y += 1
        elif (c == "L"):
            x += -1
        elif (c == "D"):
            y += -1
        if abs(x) + abs(y) >= 3:
            x = oldX
            y = oldY
    code += buttons[(x, y)]

print ("The code is", code)